document.addEventListener("DOMContentLoaded",function(){const t=document.querySelector("header");let e=window.scrollY;window.addEventListener("scroll",function(){const n=window.scrollY;Math.abs(n-e)>50&&(n>e&&n>100?t.classList.add("header-hidden"):t.classList.remove("header-hidden"),e=n)}),document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const t=document.querySelector(this.getAttribute("href"));t&&window.scrollTo({top:t.offsetTop-80,behavior:"smooth"})})})})