document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector("header"),t=e.offsetHeight;document.body.style.paddingTop=t+"px",document.documentElement.style.scrollPaddingTop=t+"px",window.addEventListener("scroll",function(){const t=window.scrollY;t>5?e.classList.add("hidden"):e.classList.remove("hidden")}),document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const s=this.getAttribute("href"),n=document.querySelector(s);if(n){const e=n.getBoundingClientRect().top+window.scrollY,s=e-t;window.scrollTo({top:s,behavior:"smooth"})}})}),window.addEventListener("scrollend",function(){window.scrollY===0&&e.classList.remove("hidden")})})