document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector("header");if(!e)return;const t=e.offsetHeight;document.documentElement.style.setProperty("--header-height",t+"px"),document.body.style.paddingTop=t+"px",window.addEventListener("scroll",function(){const t=window.scrollY;t>5?e.classList.add("hidden"):e.classList.remove("hidden")}),document.querySelectorAll('a[href^="#"]').forEach(t=>{t.addEventListener("click",function(t){t.preventDefault();const n=this.getAttribute("href"),s=document.querySelector(n);if(s){const t=document.querySelector("header").offsetHeight,o=s.getBoundingClientRect().top+window.scrollY-t;window.scrollTo({top:o,behavior:"smooth"}),n==="#"&&e.classList.remove("hidden")}})}),window.addEventListener("scrollend",function(){window.scrollY===0&&e.classList.remove("hidden")})})