document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector("header"),t=e.offsetHeight;document.body.style.paddingTop=t+"px",window.addEventListener("scroll",function(){const t=window.scrollY;t>5?e.classList.add("hidden"):e.classList.remove("hidden")}),document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const n=this.getAttribute("href"),t=document.querySelector(n);if(t){const e=document.querySelector("header").offsetHeight,n=t.offsetTop-e;window.scrollTo({top:n,behavior:"smooth"})}})})})